#include<iostream>
using namespace std;
int main()
{
	int count=0,y=0,z;
	for(int p=3;p<=1000;p++)
	{
		for(int i=1;i<1000;i++)
	{
		for(int j=i;j<1000-i;j++)
		{
			for(int k=j;k<1000-j;k++)
			{
				if(i*i+j*j==k*k&&i+j+k==p)
				{
					cout<<i<<" "<<j<<" "<<k<<" ";
					count++;
				}
			}
		}
	}
	if(y<count)
{
		y=count;
		z=p;
}
	count=0;
	}
	cout<<z;
}
